};

const fu = e => {
  var date = new Date();
  var timestamp = date.getTime();

  var re = /#\w+/gim;
  var found = e.target.value.match(re);

  var aba = /@\w+/gim;
  var founda = e.target.value.match(aba);

  var Todo = /\$\w+/gim;
  var TodoF = e.target.value.match(Todo);

  Notes = found;
  Inbox = founda;
  Todos = TodoF;

  if (Inbox != null) {
    for (let index3 = 0; index3 < Inbox.length; index3++) {
      var c = Inbox[index3];

      Inbox[index3] = c.substring(1, c.length);
      console.log(Inbox[index3]);
    }
  }

  if (Notes != null) {
    for (let index4 = 0; index4 < Notes.length; index4++) {
      var d = Notes[index4];

      Notes[index4] = d.substring(1, d.length);
      console.log(Notes[index4]);
    }
  }

  if (TodoF != null) {
    for (let index5 = 0; index5 < TodoF.length; index5++) {
      var f = Todos[index5];
      Todos[index5] = f.substring(1, f.length);

      console.log(Todos[index5]);
    }
  }

  qaa = {
    uid: qaa.uid,
    msg: e.target.value,
    timestamp: timestamp
  };
};
function CardSendmgs(props) {
  const { classes } = props;
  qaa = {
    uid: props.uid
  };

  return (
    <div>
      <Card className={classes.card}>
        <CardContent>
          <Typography
            className={classes.title}
            color="textSecondary"
            gutterBottom
          >
            New Messsage
          </Typography>
          <Input onChange={fu} multiline />
        </CardContent>
        <CardActions>
          <div />
          <Button onClick={onClick} size="small">
            Send
          </Button>
        </CardActions>
      </Card>
    </div>
  );
}

CardSendmgs.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(CardSendmgs);
