import { Container } from "unstated";

class UserContainer extends Container {
  state = {
    uid: null,
    name: null,
    username: null
  };

  setUser = (uid, name, username) => {
    this.setState({
      uid: uid,
      name: name,
      username: username
    });
  };

  su = () => {
    this.setState({ uid: "SVCCCCS" });
  };
}

export default UserContainer;
