import React, { Component } from "react";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { Redirect } from "react-router-dom";
import fire from "../Fire";
class Login extends Component {
  state = {
    email: null,
    password: null,
    signup: false,
    logged: false
  };

  CHeck = () => {
    fire.auth().onAuthStateChanged(user => {
      if (user) {
        window.alert("User IS Logged In");
        this.setState({ logged: true });
      }
    });
  };

  onChangeEmail = e => {
    this.setState({
      email: e.target.value
    });
  };

  onChangePass = e => {
    this.setState({
      password: e.target.value
    });
  };
  onLogin = e => {
    firebase
      .auth()
      .setPersistence(firebase.auth.Auth.Persistence.SESSION)
      .then(function() {
        // Existing and future Auth states are now persisted in the current
        // session only. Closing the window would clear any existing state even
        // if a user forgets to sign out.
        // ...
        // New sign-in will be persisted with session persistence.
        return firebase.auth().signInWithEmailAndPassword(this.state.e, password);
      })
      .catch(function(error) {
        // Handle Errors here.
        var errorCode = error.code;
        var errorMessage = error.message;
      });
    this.CHeck();
  };

  render() {
    return (
      <div>
        <div>
          <TextField
            id="outlined-name"
            label="Email"
            margin="normal"
            variant="outlined"
            onChange={this.onChangeEmail}
          />
        </div>
        <div>
          <TextField
            id="outlined-name"
            label="Password"
            margin="normal"
            onChange={this.onChangePass}
            variant="outlined"
          />
        </div>
        <div>
          <Button variant="contained" color="secondary" onClick={this.onLogin}>
            Login
          </Button>
          {this.state.logged ? <Redirect push to="/dashboard" /> : null}

          <a href="/signup">
            <Button variant="contained" color="secondary">
              SignUp
            </Button>
          </a>
        </div>
      </div>
    );
  }
}
export default Login;
