import React, { Component } from "react";
import "./App.css";
import scrollToComponent from "react-scroll-to-component";

class Demo extends React.Component {
  componentDidMount() {
    scrollToComponent(this.Blue, {
      offset: 0,
      align: "middle",
      duration: 500,
      ease: "inCirc"
    });
  }

  render() {
    return (
      <div className="main">
        <div className="button_group">
          <button
            onClick={() =>
              scrollToComponent(this.Violet, {
                offset: 0,
                align: "top",
                duration: 1500
              })
            }
          >
            Go To Violet
          </button>
          <button
            onClick={() =>
              scrollToComponent(this.Indigo, {
                offset: 0,
                align: "bottom",
                duration: 500,
                ease: "inExpo"
              })
            }
          >
            Go To Indigo
          </button>
          <button
            onClick={() =>
              scrollToComponent(this.Blue, {
                offset: -200,
                align: "middle",
                duration: 1500,
                ease: "inCirc"
              })
            }
          >
            Go To Blue
          </button>
          <button
            onClick={() =>
              scrollToComponent(this.Green, {
                offset: 0,
                align: "middle",
                duration: 500,
                ease: "inExpo"
              })
            }
          >
            Go To Green
          </button>
          <button
            onClick={() =>
              scrollToComponent(this.Yellow, {
                offset: 0,
                align: "top",
                duration: 1500,
                ease: "inCirc"
              })
            }
          >
            Go To Yellow
          </button>
          <button
            onClick={() =>
              scrollToComponent(this.Orange, {
                offset: 0,
                align: "top",
                duration: 500,
                ease: "inCirc"
              })
            }
          >
            Go To Orange
          </button>
        </div>
      </div>
    );
  }
}

export default Demo;
