import React, { Component } from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import classnames from "classnames";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import Avatar from "@material-ui/core/Avatar";
import IconButton from "@material-ui/core/IconButton";
import Typography from "@material-ui/core/Typography";
import red from "@material-ui/core/colors/red";
import FavoriteIcon from "@material-ui/icons/Favorite";
import ShareIcon from "@material-ui/icons/Share";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import MoreVertIcon from "@material-ui/icons/MoreVert";
import CardMedia from "@material-ui/core/CardMedia";
import fire from "../Fire";
class PolShow extends Component {
  componentWillMount() {
    var a = [];
    fire
      .database()
      .ref()
      .child("Poll")
      .on("child_added", da => {
        var k = {
          val: da.val(),
          vote: []
        };

        var Votes = [];
        Votes.push(da.val.votes);
        k = {
          val: da.val(),
          vote: Votes
        };
        console.log(k);
      });
  }

  constructor(props) {
    super(props);
    this.state = {
      array: []
    };
  }
  render() {
    return (
      <div>
        <div
          style={{
            paddingTop: 20,
            paddingBottom: 20,
            paddingLeft: 10,
            paddingRight: 10
          }}
        >
          <Card className="">
            <CardHeader
              avatar={
                <Avatar aria-label="Recipe" className={classes.avatar}>
                  R
                </Avatar>
              }
              action={
                <IconButton>
                  <MoreVertIcon />
                </IconButton>
              }
              title={this.props.uid}
              subheader={this.props.timestamp}
            />
            <CardMedia
              className="RecipeReviewCard-media-298"
              image={this.props.pic}
              title="Paella dish"
            />

            <CardContent>
              <Typography component="p">{this.props.msg}</Typography>
            </CardContent>
            <CardActions className={classes.actions} disableActionSpacing>
              <IconButton aria-label="Add to favorites">
                <FavoriteIcon />
              </IconButton>
              <IconButton aria-label="Share">
                <ShareIcon />
              </IconButton>
              <IconButton
                className={classnames(classes.expand, {
                  [classes.expandOpen]: this.state.expanded
                })}
                onClick={this.handleExpandClick}
                aria-expanded={this.state.expanded}
                aria-label="Show more"
              >
                <ExpandMoreIcon />
              </IconButton>
            </CardActions>
          </Card>
        </div>
      </div>
    );
  }
}

export default PolShow;
