import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";
import fire from "./Fire";
import SendMsg from "./ChatComponents/SendMsg";
import ChatHead from "./ChatComponents/ChatHead";
class App extends Component {
  state = {
    mess: []
  };

  componentWillMount() {
    let a = [];
    fire
      .database()
      .ref()
      .child("Msg")
      .on("child_added", da => {
        a.push({
          msg: da.child("msg").node_.value_,
          uid: da.child("uid").node_.value_
        });
        this.setState({ mess: a });
      });
  }

  render() {
    return (
      <div className="App">
        {this.state.mess.map(mess => {
          return (
            <div>
              <ChatHead>{mess.msg}</ChatHead>
            </div>
          );
        })}
        <SendMsg uid="avuceo" />
      </div>
    );
  }
}

export default App;
