import React, { Component } from "react";
import fire from "../Fire";
import ChatHead from "./ChatHead";
import Card from "./Card";
import ScrollableFeed from "react-scrollable-feed";
class ChatShow extends Component {
  state = {
    mess: []
  };
  scrollToBottom() {
    const scrollHeight = this.messageList.scrollHeight;
    const height = this.messageList.clientHeight;
    const maxScrollTop = scrollHeight - height;
    this.messageList.scrollTop = maxScrollTop > 0 ? maxScrollTop : 0;
  }

  componentDidUpdate() {
    this.scrollToBottom();
  }
  componentWillMount() {
    let a = [];
    fire
      .database()
      .ref()
      .child("Msg")
      .on("child_added", da => {
        a.push({
          msg: da.child("msg").node_.value_,
          uid: da.child("uid").node_.value_,
          timestamp: da.child("timestamp").node_.value_
        });
        this.setState({ mess: a });
      });
  }

  render() {
    return (
      <div
        className="MessageList"
        ref={div => {
          this.messageList = div;
        }}
      >
        {this.state.mess.map(mess => {
          var d = new Date(mess.timestamp);
          var s =
            d.getHours() +
            ":" +
            d.getMinutes() +
            "    " +
            d.getDate() +
            "/" +
            d.getMonth();

          return <div>{mess.timestamp}</div>;
        })}
      </div>
    );
  }
}

export default ChatShow;
