import React, { Component } from "react";
import fire from "../Fire";
import Card from "./Card";
import "../A/Style.css";
import './Cha'
class ChatShow extends Component {
  state = {
    mess: [],
    revmess: []
  };

  componentWillMount() {
    let a = [];
    fire
      .database()
      .ref()
      .child("Msg")
      .on("child_added", da => {
        a.push({
          msg: da.child("msg").node_.value_,
          uid: da.child("uid").node_.value_,
          timestamp: da.child("timestamp").node_.value_,
          pic: da.child("pic").node_.value_
        });
        this.setState({ mess: a });
      });

    this.setState({
      mess: this.state.mess,
      revmess: this.state.mess.reverse
    });
  }
  render() {
    return (
      <div>
        <div className="col-lg-8">
          <div className="card">
            <div className="card-body">
              <h4 className="card-title">Recent Chats</h4>
              <div
                className="chat-box scrollable position-relative ps-container ps-theme-default "
                style={{ height: "475px", webkitscrol: "auto" }}
                data-ps-id="86ffbaf8-776f-0a99-e662-7f15e24d9a84"
              >
                {/*chat Row */}
                <ul className="chat-list">
                  {/*chat Row */}{" "}
                  {this.state.mess
                    .slice(0)
                    .reverse()
                    .map(mess => {
                      var d = new Date(mess.timestamp);
                      var s =
                        d.getHours() +
                        ":" +
                        d.getMinutes() +
                        "    " +
                        d.getDate() +
                        "/" +
                        d.getMonth();

                      return (
                        <Card
                          uid={mess.uid}
                          msg={mess.msg}
                          pic={mess.pic}
                          timestamp={s}
                        />
                      );
                    })}
                </ul>
                <div
                  className="ps-scrollbar-x-rail"
                  style={{ left: "0px", bottom: "0px" }}
                >
                  <div
                    className="ps-scrollbar-x"
                    tabIndex={0}
                    style={{ left: "0px", width: "0px" }}
                  />
                </div>
                <div
                  className="ps-scrollbar-y-rail"
                  style={{ top: "0px", right: "3px", height: "475px" }}
                >
                  <div
                    className="ps-scrollbar-y"
                    tabIndex={0}
                    style={{ top: "0px", height: "427px" }}
                  />
                </div>
              </div>
            </div>
            <div className="card-body border-top">
              <div className="row">
                <div className="col-9">
                  <div className="input-field m-t-0 m-b-0">
                    <input
                      type="text"
                      id="textarea1"
                      placeholder="Type and enter"
                      className="form-control
                                    border-0"
                    />
                  </div>
                </div>
                <div className="col-3">
                  <a
                    className="btn-circle btn-lg btn-cyan float-right text-white"
                    href="javascript:void(0)"
                  >
                    <i className="fas fa-paper-plane" />
                  </a>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default ChatShow;
