import React, { Component } from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import classnames from "classnames";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardContent from "@material-ui/core/CardContent";
import Avatar from "@material-ui/core/Avatar";
import IconButton from "@material-ui/core/IconButton";

import MoreVertIcon from "@material-ui/icons/MoreVert";
import CardMedia from "@material-ui/core/CardMedia";
import fire from "../Fire";
import Grid from "@material-ui/core/Grid";
import Button from "@material-ui/core/Button";

var s = 0;
var voted = false;
class PolShow extends Component {
  check() {}
  as(ref, title) {
    fire
      .database()
      .ref()
      .child("Poll")
      .child(ref)
      .child("vote")
      .child("Username")
      .set(title);

    fire
      .database()
      .ref()
      .child("Poll")
      .child(ref)
      .child("voteR")
      .child(title)
      .child("Username")
      .set("Username");
  }
  componentWillMount() {
    var a = [];
    var s = [];

    fire
      .database()
      .ref()
      .child("Poll")
      .on("child_added", da => {
        var check = false;
        fire
          .database()
          .ref()
          .child("Poll")
          .child(da.key)
          .child("vote")
          .on("child_added", d => {
            if (d.key === "Username") {
              check = true;
            }
          });

        a.push({ a: da.val(), b: da.key, c: false });

        this.setState({ array: a });
      });
  }

  constructor(props) {
    super(props);
    this.state = {
      array: []
    };
  }
  render() {
    return (
      <div>
        {this.state.array.map(array => {
          return (
            <div style={{ padding: 30 }}>
              <Card className="RecipeReviewCard-card-297">
                <CardHeader
                  avatar={
                    <Avatar
                      aria-label="Recipe"
                      className="RecipeReviewCard-avatar-302"
                    >
                      R
                    </Avatar>
                  }
                  action={
                    <IconButton>
                      <MoreVertIcon />
                    </IconButton>
                  }
                  title="A"
                />
                <CardMedia
                  className="RecipeReviewCard-media-298"
                  title="Paella dish"
                />
                <CardContent>
                  <Grid container>
                    <Grid item xs={4}>
                      {array.a.title.Pol.map(Pol => {
                        return (
                          <div style={{ padding: 7 }}>
                            <Button
                              disabled={array.c}
                              onClick={this.as.bind(this, array.b, Pol)}
                            >
                              {Pol}{" "}
                            </Button>{" "}
                            {fire
                              .database()
                              .ref()
                              .child("Poll")
                              .child(array.b)
                              .child("voteR")
                              .child(Pol)
                              .on("value", kak => {
                                s = kak.numChildren();
                                console.log(kak.numChildren());
                              })}
                            <h1>{s}</h1>
                          </div>
                        );
                      })}
                    </Grid>
                    {fire
                      .database()
                      .ref()
                      .child("Poll")
                      .child(array.b)
                      .child("vote")
                      .on("child_added", da => {
                        console.log(da.val());
                      })}
                  </Grid>
                </CardContent>
              </Card>
            </div>
          );
        })}
      </div>
    );
  }
}

export default PolShow;
