import React, { Component } from "react";
import fire from "../Fire";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemSecondaryAction from "@material-ui/core/ListItemSecondaryAction";
import ListItemText from "@material-ui/core/ListItemText";
import Checkbox from "@material-ui/core/Checkbox";
import IconButton from "@material-ui/core/IconButton";
import Info from "@material-ui/icons/Info";
import Collapse from "@material-ui/core/Collapse";
import { withStyles } from "@material-ui/core/styles";
import ClassesShrthand from "./Not";

import "./No.css";

// We can use inline-style
const style = {
  width: "100%",
  background: "black"
};

const StyledButton = withStyles({
  root: {
    background: "linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)",
    borderRadius: 3,
    border: 0,
    color: "white",
    height: 48,
    padding: "0 30px",
    boxShadow: "0 3px 5px 2px rgba(255, 105, 135, .3)"
  },
  label: {
    textTransform: "capitalize"
  }
});
class NoteShow extends Component {
  state = {
    Notes: []
  };

  componentWillMount() {
    var a = [];
    fire
      .database()
      .ref()
      .child("Notes")
      .on("child_added", da => {
        a.push({
          Note: da.child("Note").node_.value_,
          timestamp: da.child("timestamp").node_.value_,
          uid: da.child("uid").node_.value_
        });
        console.log(da);
        this.setState({ Notes: a });
      });

    this.setState({ profiles: a });
  }
  render() {
    return (
      <div>
        <List>
          {this.state.Notes.map(Notes => {
            return (
              <Card className={classes.card}>
              <CardHeader
                avatar={
                  <Avatar aria-label="Recipe" className={classes.avatar}>
                    R
                  </Avatar>
                }
                action={
                  <IconButton>
                    <MoreVertIcon />
                  </IconButton>
                }
                title={Notes.uid}
                subheader={Notes.timestamp}
              />

              <CardContent>
                <Typography component="p">{}</Typography>
              </CardContent>
              <CardActions className={classes.actions} disableActionSpacing>
                <IconButton aria-label="Add to favorites">
                  <FavoriteIcon />
                </IconButton>
                <IconButton aria-label="Share">
                  <ShareIcon />
                </IconButton>
                <IconButton
                  className={classnames(classes.expand, {
                    [classes.expandOpen]: this.state.expanded
                  })}
                  onClick={this.handleExpandClick}
                  aria-expanded={this.state.expanded}
                  aria-label="Show more"
                >
                  <ExpandMoreIcon />
                </IconButton>
              </CardActions>
            </Card>
            );
          })}
        </List>
      </div>
    );
  }
}

export default NoteShow;
