import React, { Component } from "react";
import fire from "../Fire";
class TodoShow extends Component {
  state = {};

  componentDidMount() {
    var a = [];

    fire
      .database()
      .ref()
      .child("Todo")
      .on("child_added", da => {
        a.push({
          todo: da.val().info.todo,
          timestap: da.val().info.timestamp,
          key: da.val().info.key,
          uid: da.val().info.uid
        });
      });

    this.setState({ info: a });
  }

  render() {
    return <div>  <Card style={{ minWidth: 380 }}>
    <CardHeader
      avatar={<Avatar aria-label="Recipe">R</Avatar>}
      action={
        <IconButton>
          <MoreVertIcon />
        </IconButton>
      }
      title={Notes.uid}
      subheader={Notes.timestamp}
    />

    <CardContent>
      <Typography variant="h6" gutterBottom>
        {Notes.Note}{" "}
      </Typography>
    </CardContent>
  </Card></div>;
  }
}

export default TodoShow;
