    this.setState({ Info: a });
  }

  s(r) {
    fire
      .database()
      .ref()
      .child("Todo")
      .child(r)
      .child("Completed")
      .child("uid")
      .set("username");
  }

  render() {
    return (
      <div>
        {console.log(this.state)}
        {this.state.Info.map(Info => {
          return (
            <Card key={Info.ref}>
              <Grid container>
                <Grid item xs={1}>
                  <Checkbox onClick={this.s.bind(this, Info.ref)} />
                </Grid>
                <Grid item xs={11}>
                  <CardHeader title={Info.uid} subheader={Info.timestamp} />

                  <CardContent>
                    <Typography variant="h6" gutterBottom>
                      {Info.todo}
                    </Typography>
                  </CardContent>
                </Grid>{" "}
              </Grid>
            </Card>
          );
        })}
      </div>
    );
  }
}

export default TodoShow;
