import React, { Component } from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import Input from "@material-ui/core/Input";
import "./Chat.css";
const styles = {
  card: {
    minWidth: 300
  },
  bullet: {
    display: "inline-block",
    margin: "0 2px",
    transform: "scale(0.8)"
  },
  title: {
    fontSize: 14
  },
  pos: {
    marginBottom: 12
  }
};

class CardSendmgs extends Component {
  constructor(props) {
    super(props);
    this.state = { msg: null, uid: null, timestamp: null };

    onChange = e => {
      this.setState({ msg: e.target.value });
      this.setState({ uid: this.props.uid });
      var date = new Date();
      var timestamp = date.getTime();
      this.setState({ timestamp: timestamp });
    };

    onClick = () => {
      fire
        .database()
        .ref()
        .child("Msg")
        .push(this.state);
    };

    const { classes } = props;
    const bull = <span className={classes.bullet}>â€¢</span>;
  }

  render() {
    return (
      <div>
        <Card className={classes.card}>
          <CardContent>
            <Typography
              className={classes.title}
              color="textSecondary"
              gutterBottom
            >
              New Messsage
            </Typography>
            <Input multiline onChange={this.onChange} />
          </CardContent>
          <CardActions>
            <div className="SendPos">
              <Button onClick={this.onClick} size="small">
                Send
              </Button>
            </div>
          </CardActions>
        </Card>
      </div>
    );
  }
}

CardSendmgs.propTypes = {
  classes: PropTypes.object.isRequired
};
export default withStyles(styles)(CardSendmgs);
