import React, { Component } from "react";
import fire from "../Fire";
class TodoShow extends Component {
  state = {};

  componentDidMount() {
    var a = [];

    fire
      .database()
      .ref()
      .child("Todo")
      .on("child_added", da => {
        a.push({
          todo: da.val().infotodo,
          timestap: da.val().timestamp,
          key: da.val().key,
          uid: da.val().uid
        });
      });

    this.setState({ info: a });
  }

  render() {
    return <div>{console.log(this.state)}</div>;
  }
}

export default TodoShow;
