          </Typography>
          <Input multiline />
        </CardContent>
        <CardActions>
          <div className="SendPos">
            <Button size="small">Send</Button>
          </div>
        </CardActions>
      </Card>
    </div>
  );
}

CardSendmgs.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(CardSendmgs);
