import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import classnames from "classnames";
import Card from "@material-ui/core/Card";
import Button from "@material-ui/core/Button";
import CardHeader from "@material-ui/core/CardHeader";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import Avatar from "@material-ui/core/Avatar";
import IconButton from "@material-ui/core/IconButton";
import Typography from "@material-ui/core/Typography";
import red from "@material-ui/core/colors/red";
import FavoriteIcon from "@material-ui/icons/Favorite";
import ShareIcon from "@material-ui/icons/Share";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import MoreVertIcon from "@material-ui/icons/MoreVert";
import CardMedia from "@material-ui/core/CardMedia";
import "../A/Sb.css";

import "./Chat.css";
import { Paper } from "@material-ui/core";

const styles = theme => ({
  card: {
    minWidth: 400
  },
  media: {
    height: 0,
    paddingTop: "56.25%" // 16:9
  },
  actions: {
    display: "flex"
  },
  expand: {
    transform: "rotate(0deg)",
    transition: theme.transitions.create("transform", {
      duration: theme.transitions.duration.shortest
    }),
    marginLeft: "auto",
    [theme.breakpoints.up("sm")]: {
      marginRight: -8
    }
  },
  expandOpen: {
    transform: "rotate(180deg)"
  },
  avatar: {
    backgroundColor: red[500]
  }
});

class RecipeReviewCard extends React.Component {
  state = { expanded: false };

  handleExpandClick = () => {
    this.setState(state => ({ expanded: !state.expanded }));
  };

  render() {
    function UserGreeting(p) {
      if (p != null) {
        return (
          <CardMedia className={classes.media} image={p} title="Paella dish" />
        );
      } else return null;
    }

    const { classes } = this.props;

    return (
      <div>
        <div style={{ width: "95%" }} class="card card-stats">
          <div class="card-header card-header-danger card-header-icon">
            <div style={{ width: 50, height: 50 }} class="card-icon">
            </div>
            <p class="card-category">A"this.props.name}</p>
            <h3 class="card-title">ops.username}</h3>
          </div>
          {this.props.msg}{" "}
          <div class="card-footer">
            <div class="stats">
              <i class="material-icons">local_offer</i>
              {this.props.timestamp}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

RecipeReviewCard.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(RecipeReviewCard);
