import React, { Component } from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import classnames from "classnames";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import Avatar from "@material-ui/core/Avatar";
import IconButton from "@material-ui/core/IconButton";
import Typography from "@material-ui/core/Typography";
import red from "@material-ui/core/colors/red";
import FavoriteIcon from "@material-ui/icons/Favorite";
import ShareIcon from "@material-ui/icons/Share";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import MoreVertIcon from "@material-ui/icons/MoreVert";
import CardMedia from "@material-ui/core/CardMedia";
import fire from "../Fire";
import Grid from "@material-ui/core/Grid";
import Button from "@material-ui/core/Button";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Radio from "@material-ui/core/Radio";
import { keys } from "@material-ui/core/styles/createBreakpoints";
import { PieChart } from "react-easy-chart";

var voted = false;
class PolShow extends Component {
  check() {}
  as(ref, title) {
    fire
      .database()
      .ref()
      .child("Poll")
      .child(ref)
      .child("vote")
      .child("Username")
      .set(title);
  }
  componentWillMount() {
    var a = [];
    fire
      .database()
      .ref()
      .child("Poll")
      .on("child_added", da => {
        a.push({ a: da.val(), b: da.key });

        // var k = {
        //   val: da.val(),
        //   vote: []
        // };

        // var Votes = [];
        // Votes.push(da.val.votes);
        // k = {
        //   val: da.val(),
        //   vote: Votes
        // };
        // var aa = [];

        // aa.push(k);

        this.setState({ array: a });
      });
  }

  constructor(props) {
    super(props);
    this.state = {
      array: []
    };
  }
  render() {
    return (
      <div>
        {this.state.array.map(array => {
          return (
            <div style={{ padding: 30 }}>
              <Card className="RecipeReviewCard-card-297">
                <CardHeader
                  avatar={
                    <Avatar
                      aria-label="Recipe"
                      className="RecipeReviewCard-avatar-302"
                    >
                      R
                    </Avatar>
                  }
                  action={
                    <IconButton>
                      <MoreVertIcon />
                    </IconButton>
                  }
                  title="A"
                />
                <CardMedia
                  className="RecipeReviewCard-media-298"
                  title="Paella dish"
                />
                <CardContent>
                  <Grid container>
                    <Grid item xs={4}>
                      {array.a.title.Pol.map(Pol => {
                        return (
                          <div style={{ padding: 7 }}>
                            <Button onClick={as}>{Pol}</Button>
                          </div>
                        );
                      })}
                    </Grid>

                    <Grid  item xs={4}>





                      <PieChart
                        labels
                        data={[
                          { key: "A", value: 100, color: "#aaac84" },
                          { key: "B", value: 200, color: "#dce7c5" },
                          { key: "C", value: 50, color: "#e3a51a" }
                        ]}
                        styles={{
                          ".chart_text": {
                            fontSize: "1em",
                            fill: "#fff"
                          }
                        }}
                      />
                    </Grid>
                  </Grid>
                </CardContent>
              </Card>
            </div>
          );
        })}
      </div>
    );
  }
}

export default PolShow;
